import React, { useState } from 'react'
import './index.css'
import axios from 'axios';
import BasketItem from './BasketItem';

const Basket = ({ onClickBasket,items }) => {
	
	const editItem = (newItems) => {
		console.log('items', newItems);
		axios.post(`https://6411c0db5d0c49b7c5b9068c.mockapi.io/rwerwe/`, items)
			.catch(() => {
				alert('Ошибка при редактировании!');
			})
	}
	
	const [editMode, setEditMode] = useState(false);

	return (
	<div className='container_basket' >
		<div className='aside_basket'>
			<div className='row_aside'>
				<div className='row_header'>
						<h1>Корзина</h1>
						<button onClick={() => setEditMode(true)}>Редактировать</button>
						<button onClick={() => editItem(items)}>Сохранить</button>
						<img onClick={onClickBasket} src='https://cdn-icons-png.flaticon.com/128/70/70206.png' style={{ cursor: 'pointer' }} width='50px' height='50px'/>
				</div>
				<div>
				{(items.map((item, index) =>
					<BasketItem
						key={index}
						editMode={editMode}
						item={item}
					/>
				))}
				</div>
			</div>
		</div>
	</div>

  )
}

export default Basket
