import React, { useState } from 'react'
import './index.css'
import axios from 'axios';

const Basket = ({ onClickBasket,items }) => {
	
	const [inputValue, setInputValue] = useState();

	const removeList = (items) => {
		{
			axios.delete(`https://6411c0db5d0c49b7c5b9068c.mockapi.io/rwerwe/${items}`)
		}
		setInterval(() => {
			window.location.reload()
		}, 1000)
		}

	const editItem = (name) => {
		axios.put(`https://6411c0db5d0c49b7c5b9068c.mockapi.io/rwerwe/${itemEdit.id}`, {
			name: name
		}).catch(() => {
			alert('Ошибка при редактировании!');
		})
		setInterval(() => {
			window.location.reload()
		}, 500)
	}
	
	return (
	<div className='container_basket' >
		<div className='aside_basket'>
			<div className='row_aside'>
				<div className='row_header'>
					<h1>Корзина</h1>
						<img onClick={onClickBasket} src='https://cdn-icons-png.flaticon.com/128/70/70206.png' style={{ cursor: 'pointer' }} width='50px' height='50px'/>
				</div>
				<div>
				{(items.map((item, index) =>
						<div className='aside'
							key={index}>
							<img src={item.imageUrl} width='60px' height='60px' />
							<input
								value={item.name}
								onChange={e => editItem(e.target.value)}
								type="text"
							/>
							<p>{item.price}</p>
							<img onClick={() => removeList(item.id)} src='https://cdn-icons-png.flaticon.com/128/70/70206.png' style={{ cursor: 'pointer' }} width='30px' height='30px' />
						</div>
				))}
				</div>
			</div>
		</div>
	</div>

  )
}

export default Basket
