import { Card } from './components/card/Card';
import './App.css';
import { Header } from './components/header/Header';
import Basket from './components/basket/Basket';
import { useState } from 'react';



function App() {

	const [list, setList] = useState(null);
	const [openBasket, setOpenBasket] = useState(false);

	useEffect(() => {
		axios.get('https://62abab0dbd0e5d29af13d0c5.mockapi.io/cart')
			.then(({ data }) => {
				setLists(data);
				setFilteredCarts(data);
			});
			axios.get('https://62abab0dbd0e5d29af13d0c5.mockapi.io/items')
			.then(({ data }) => {
				setTask(data);
				setTaskAdd(data);
			});
	}, []);

	return (
	<div className='container'>
		<Header onClickBasket={() => setOpenBasket(!openBasket)} />
		<div className='Card'>
			<Card/>
		</div>
		{openBasket ? <Basket onClickBasket={() => setOpenBasket(!openBasket)} /> : null}
	</div>
  )
}

export default App;
